import { feature, featureCollection } from '@turf/turf';
import { parse, stringify } from 'wellknown';
export function GeoJSON2Wkt(fc) {
  return fc.features.map(function (item) {
    return stringify(item.geometry);
  }).join(';')
  //@ts-ignore
  .replaceAll(' (', '(').replaceAll(', ', ',');
}
export function Wkt2GeoJSON(wkt) {
  var fc = featureCollection([]);
  wkt.split(/[;\n]/g).forEach(function (item) {
    if (item) {
      var geometry = parse(item);
      if (geometry) {
        fc.features.push(feature(geometry, {}));
      }
    }
  });
  return fc;
}